mapper.update:
update t_funds
uid = #{uid}, amount = #{amount}, create_time = #{createTime}, update_time = #{updateTime}, category = #{category}, type = #{type}, status = #{status},
where id=#{id}

update t_funds

	uid = #{new.uid},
	amount = #{new.amount},
	create_time = #{new.createTime},
	update_time = #{new.updateTime},
	category = #{new.category},
	type = #{new.type},
	status = #{new.status},


	where id=#{old.id}
	and uid = #{old.uid}
	and amount = #{old.amount}
	and create_time = #{old.createTime}
	and update_time = #{old.updateTime}
	and category = #{old.category}
	and type = #{old.type}
	and status = #{old.status}
