mapper.update:
update t_oj
pid = #{pid}, uid = #{uid}, name = #{name}, difficulty = #{difficulty}, oj_type = #{ojType}, type = #{type}, pre_time = #{preTime}, use_time = #{useTime}, standalone = #{standalone}, study = #{study}, link = #{link}, importance = #{importance}, create_time = #{createTime}, update_time = #{updateTime}, status = #{status},
where id=#{id}

update t_oj

    pid = #{new.pid},
    uid = #{new.uid},
    name = #{new.name},
    difficulty = #{new.difficulty},
    oj_type = #{new.ojType},
    type = #{new.type},
    pre_time = #{new.preTime},
    use_time = #{new.useTime},
    standalone = #{new.standalone},
    study = #{new.study},
    link = #{new.link},
    importance = #{new.importance},
    create_time = #{new.createTime},
    update_time = #{new.updateTime},
    status = #{new.status},


    id=#{old.id}
    and pid = #{old.pid}
    and uid = #{old.uid}
    and name = #{old.name}
    and difficulty = #{old.difficulty}
    and oj_type = #{old.ojType}
    and type = #{old.type}
    and pre_time = #{old.preTime}
    and use_time = #{old.useTime}
    and standalone = #{old.standalone}
    and study = #{old.study}
    and link = #{old.link}
    and importance = #{old.importance}
    and create_time = #{old.createTime}
    and update_time = #{old.updateTime}
    and status = #{old.status}

mapper.select:
select id, pid, uid, name, difficulty, oj_type as ojType, type, pre_time as preTime, use_time as useTime,
standalone, study, link, create_time as createTime, update_time as updateTime, status
from t_oj

    uid=#{uid} and status != 4
    and create_time  >=  #{begin}
    and create_time  <=  #{end}

order by id desc
limit #{offset}, #{count}

select count(1)
from t_oj

    uid=#{uid} and status != 4
    and create_time  >=  #{begin}
    and create_time  <=  #{end}
