mapper.update:
update t_todo_group
uid = #{uid}, value = #{value}, finish_value = #{finishValue}, total_time = #{totalTime}, max_time = #{maxTime}, min_priority = #{minPriority}, create_time = #{createTime}, update_time = #{updateTime}, is_private = #{isPrivate}, status = #{status},
where id=#{id}

update t_todo_group

    uid = #{new.uid},
    value = #{new.value},
    finish_value = #{new.finishValue},
    total_time = #{new.totalTime},
    max_time = #{new.maxTime},
    min_priority = #{new.minPriority},
    create_time = #{new.createTime},
    update_time = #{new.updateTime},
    is_private = #{new.isPrivate},
    status = #{new.status},


    where id=#{old.id}
    and uid = #{old.uid}
    and value = #{old.value}
    and finish_value = #{old.finishValue}
    and total_time = #{old.totalTime}
    and max_time = #{old.maxTime}
    and min_priority = #{old.minPriority}
    and create_time = #{old.createTime}
    and update_time = #{old.updateTime}
    and is_private = #{old.isPrivate}
    and status = #{old.status}
