mapper.update:
update t_todo
task = #{task}, value = #{value}, estimate_time = #{estimateTime}, reality_time = #{realityTime}, group_id = #{groupId}, create_time = #{createTime}, update_time = #{updateTime}, priority = #{priority}, status = #{status},
where id=#{id}

update t_todo

    task = #{new.task},
    value = #{new.value},
    estimate_time = #{new.estimateTime},
    reality_time = #{new.realityTime},
    group_id = #{new.groupId},
    create_time = #{new.createTime},
    update_time = #{new.updateTime},
    priority = #{new.priority},
    status = #{new.status},


    where id=#{old.id}
    and task = #{old.task}
    and value = #{old.value}
    and estimate_time = #{old.estimateTime}
    and reality_time = #{old.realityTime}
    and group_id = #{old.groupId}
    and create_time = #{old.createTime}
    and update_time = #{old.updateTime}
    and priority = #{old.priority}
    and status = #{old.status}
