where id=#{id}

update t_user

	user_name = #{new.userName},
	password = #{new.password},
	phone_number = #{new.phoneNumber},
	email = #{new.email},
	create_time = #{new.createTime},
	update_time = #{new.updateTime},
	type = #{new.type},
	status = #{new.status},


	where id=#{old.id}
	and user_name = #{old.userName}
	and password = #{old.password}
	and phone_number = #{old.phoneNumber}
	and email = #{old.email}
	and create_time = #{old.createTime}
	and update_time = #{old.updateTime}
	and type = #{old.type}
	and status = #{old.status}
